import User from "../models/user.js";
import jwt from "jsonwebtoken";
import bcrypt from "bcryptjs";
import dotenv from "dotenv";

dotenv.config();

// âœ… Generate JWT Token
const generateToken = (user) => {
  return jwt.sign({ id: user._id, role: user.role }, process.env.JWT_SECRET, {
    expiresIn: "1d",
  });
};

//SIGNUP
export const signup = async (req, res) => {
    try {
      const { firstName, lastName, email, password, role } = req.body;
  
      if (!firstName || !lastName || !email || !password) {
        return res.status(400).json({ error: "All fields are required" });
      }
  
      const existingUser = await User.findOne({ email });
      if (existingUser) {
        return res.status(400).json({ error: "User already exists" });
      }
  
      const hashedPassword = await bcrypt.hash(password, 12);
  
      console.log("âœ… Hashed Password Before Saving:", hashedPassword); // ðŸ” Debugging
  
      const newUser = new User({
        firstName,
        lastName,
        email,
        password: hashedPassword, // âœ… Ensure hashed password is stored
        role: role || "user",
      });
  
      await newUser.save();
      console.log("âœ… New User Created in DB:", newUser);
  
      res.status(201).json({
        message: "User registered successfully",
        user: { id: newUser._id, firstName, lastName, email, role: newUser.role },
        token: generateToken(newUser),
      });
    } catch (error) {
      console.error("âŒ Signup Error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  };
 
  //SIGNIN
export const signin = async (req, res) => {
    try {
      const { email, password } = req.body;
  
      console.log("ðŸ” Entered Email:", email);
      console.log("ðŸ” Entered Password:", password);
  
      // âœ… Ensure email and password are provided
      if (!email || !password) {
        return res.status(400).json({ error: "Email and password are required" });
      }
  
      const user = await User.findOne({ email });
  
      console.log("ðŸ” Fetched User from DB:", user);
  
      if (!user) {
        return res.status(401).json({ error: "Invalid email or password" });
      }
  
      const isPasswordValid = await bcrypt.compare(password, user.password);
  
      console.log("ðŸ” Stored Hashed Password:", user.password);
      console.log("ðŸ” Password Match Status:", isPasswordValid);
  
      if (!isPasswordValid) {
        return res.status(401).json({ error: "Invalid email or password" });
      }
  
      res.status(200).json({
        message: "Login successful",
        user: {
          id: user._id,
          firstName: user.firstName,
          lastName: user.lastName,
          email: user.email,
          role: user.role,
        },
        token: generateToken(user),
      });
    } catch (error) {
      console.error("âŒ Sign-in Error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  };

  //STAFF USER
import { generateAuthToken } from "../utils/jwtHelper.js"; 
// âœ… Create Staff (Cashier) - Admin Only
export const createStaff = async (req, res) => {
  try {
    // âœ… Ensure only admins can create staff
    if (!req.user || req.user.role !== "admin") {
      return res.status(403).json({ error: "Access denied. Admins only." });
    }

    const { firstName, lastName, email, password } = req.body;

    // âœ… Ensure all fields are provided
    if (!firstName || !lastName || !email || !password) {
      return res.status(400).json({ error: "All fields are required" });
    }

    // âœ… Check if user already exists
    const existingUser = await User.findOne({ email });
    if (existingUser) {
      return res.status(400).json({ error: "User already exists" });
    }

    try {
      // âœ… Hash password before saving
      const hashedPassword = await bcrypt.hash(password, 10);

      // âœ… Create staff user
      const staff = new User({
        firstName,
        lastName,
        email,
        password: hashedPassword,
        role: "staff",
      });

      await staff.save();

      // âœ… Generate token
      const token = generateAuthToken({ id: staff._id, role: "staff" });

      res.status(201).json({
        message: "Staff account created successfully",
        user: {
          id: staff._id,
          firstName,
          lastName,
          email,
          role: "staff",
        },
        token,
      });
    } catch (hashError) {
      console.error("âŒ Password Hashing Error:", hashError);
      return res.status(500).json({ error: "Failed to hash password" });
    }
  } catch (error) {
    console.error("âŒ Create Staff Error:", error);
    res.status(500).json({ error: "Internal server error" });
  }
};
  
// âœ… Request Password Reset (Send Reset Token via Email)
export const requestPasswordReset = async (req, res) => {
  try {
    const { email } = req.body;

    // âœ… Check if user exists
    const user = await User.findOne({ email });
    if (!user) {
      return res.status(404).json({ error: "User not found" });
    }

    // âœ… Generate reset token
    const resetToken = jwt.sign({ id: user._id }, process.env.JWT_SECRET, { expiresIn: "15m" });

    // âœ… Send email (Simulated using console.log, replace with nodemailer for real emails)
    console.log(`ðŸ”¹ Password Reset Token for ${email}: ${resetToken}`);

    res.status(200).json({ message: "Password reset link sent to email", resetToken });
  } catch (error) {
    console.error("âŒ Password Reset Request Error:", error);
    res.status(500).json({ error: "Internal server error" });
  }
};

// âœ… Reset Password (Using Token)
export const resetPassword = async (req, res) => {
  try {
    const { token, newPassword } = req.body;

    // âœ… Verify Token
    const decoded = jwt.verify(token, process.env.JWT_SECRET);
    const user = await User.findById(decoded.id);
    if (!user) {
      return res.status(404).json({ error: "Invalid or expired token" });
    }

    // âœ… Hash new password
    const hashedPassword = await bcrypt.hash(newPassword, 10);
    user.password = hashedPassword;
    await user.save();

    res.status(200).json({ message: "Password reset successful" });
  } catch (error) {
    console.error("âŒ Password Reset Error:", error);
    res.status(500).json({ error: "Invalid or expired token" });
  }
};

