const getTransactions = async (req, res) => {
  try {
    if (!req.user?.id) {
      console.error("âŒ Missing User ID in Token:", req.user);
      return res.status(403).json({
        error: "Invalid token: User ID missing"
      });
    }
    console.log("ðŸ” Token Decoded:", req.user);
    const {
      accountId
    } = req.params;
    const account = await _account.default.findOne({
      _id: accountId,
      owner: req.user.id
    });
    if (!account) return res.status(404).json({
      error: "Account not found or unauthorized"
    });
    const transactions = await _Transaction.default.find({
      accountId
    });
    console.log("ðŸš€ Retrieved Transactions:", transactions); // Debug log

    if (!transactions || !Array.isArray(transactions)) {
      throw new Error("Transactions not found or invalid format");
    }
    const sortedTransactions = transactions.sort((a, b) => b.createdAt - a.createdAt); // Sorting locally

    res.status(200).json({
      data: sortedTransactions
    });
  } catch (error) {
    console.error("âŒ Get Transactions Error:", error.message);
    res.status(500).json({
      error: "Internal server error"
    });
  }
};

