  try {
    if (!req.user?.id) {
      console.error("âŒ Missing User ID in Token:", req.user);
      return res.status(403).json({
        error: "Invalid token: User ID missing"
      });
    }
    console.log("ðŸ” Token Decoded:", req.user);
    const {
      transactionId
    } = req.params;
    const transaction = await _Transaction.default.findById(transactionId);
    if (!transaction) return res.status(404).json({
      error: "Transaction not found"
    });
    const account = await _account.default.findOne({
      _id: transaction.accountId,
      owner: req.user.id
    });
    if (!account) return res.status(403).json({
      error: "Unauthorized access to transaction"
    });
    res.status(200).json({
      data: transaction
    });
  } catch (error) {
    console.error("âŒ Get Transaction Error:", error);
    res.status(500).json({
      error: "Internal server error"
    });
  }
};
