import Transaction from "../models/Transaction.js";
import Account from "../models/account.js";

// âœ… Deposit Money (Credit) - Staff Only
export const creditAccount = async (req, res) => {
  try {
    if (!req.user?.id) {
      console.error("âŒ Missing User ID in Token:", req.user);
      return res.status(403).json({ error: "Invalid token: User ID missing" });
    }
    console.log("ðŸ” Token Decoded:", req.user);

    const { accountId } = req.params;
    const { amount } = req.body;

    if (req.user.role !== "staff") {
      return res.status(403).json({ error: "Access denied. Only staff can process transactions" });
    }

    if (!amount || amount <= 0) {
      return res.status(400).json({ error: "Invalid amount. Must be greater than zero." });
    }

    const account = await Account.findById(accountId);
    if (!account) return res.status(404).json({ error: "Account not found" });

    account.balance += amount;
    await account.save();

    const transaction = await Transaction.create({
      accountId,
      cashierId: req.user.id,
      type: "credit",
      amount,
      newBalance: account.balance,
    });

    res.status(201).json({
      message: "Account credited successfully",
      data: transaction,
    });
  } catch (error) {
    console.error("âŒ Credit Transaction Error:", error);
    res.status(500).json({ error: "Internal server error" });
  }
};

// âœ… Withdraw Money (Debit) - Staff Only
export const debitAccount = async (req, res) => {
  try {
    if (!req.user?.id) {
      console.error("âŒ Missing User ID in Token:", req.user);
      return res.status(403).json({ error: "Invalid token: User ID missing" });
    }
    console.log("ðŸ” Token Decoded:", req.user);

    const { accountId } = req.params;
    const { amount } = req.body;

    if (req.user.role !== "staff") {
      return res.status(403).json({ error: "Access denied. Only staff can process transactions" });
    }

    if (!amount || amount <= 0) {
      return res.status(400).json({ error: "Invalid amount. Must be greater than zero." });
    }

    const account = await Account.findById(accountId);
    if (!account) return res.status(404).json({ error: "Account not found" });

    if (account.balance < amount) {
      return res.status(400).json({ error: "Insufficient funds" });
    }

    account.balance -= amount;
    await account.save();

    const transaction = await Transaction.create({
      accountId,
      cashierId: req.user.id,
      type: "debit",
      amount,
      newBalance: account.balance,
    });

    res.status(201).json({
      message: "Account debited successfully",
      data: transaction,
    });
  } catch (error) {
    console.error("âŒ Debit Transaction Error:", error);
    res.status(500).json({ error: "Internal server error" });
  }
};

